uint64_t BoardHash::operator()(const Board& board) const
{
	const char* boardRepresentation = board.representation();
	uint64_t hash = 0;

	for (int i = 0; i < 64; i++) 
	{
		size_t index = boardRepresentation[i];
		if (index != NN)
		{
			hash ^= zobristTable[i][index];
		}
	}

	return hash;
}
